INSERT INTO `commodity_review_info` VALUES (711, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2019款', 'https://www.zhihu.com/answer/919544470', '<em>HUAWEI</em> <em>MateBook</em> <em>X</em> <em>Pro</em> Linux版和<em>2019款</em>有什么区别？', NULL, '便宜300，随机附赠一个u盘装win10，Linux版值得入手');
INSERT INTO `commodity_review_info` VALUES (712, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1034424764', '如何看待新款华为matebook<em>14</em> <em>2020款</em>？', NULL, '关键的看实际使用的体验。个人认为<em>matebook14</em> <em>2020</em>是一款优缺点都很明显的机器，只值这个钱，没有香不香的说法，无非是华为刀法比较好，对用户需求把握更精准而已');
INSERT INTO `commodity_review_info` VALUES (713, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/993914410', '如何评价<em>华为</em>推出全新的 <em>MateBook</em> 13/<em>14</em> <em>2020</em> <em>款</em>？值得入手吗？', NULL, '成为全新的“真香本”？ MateBook 13 即13英寸轻薄本；<em>MateBook</em> <em>14</em> 即14英寸轻薄本。 前情提要，MateBook 13/<em>14</em> <em>2020</em> 主要升级了以下内容');
INSERT INTO `commodity_review_info` VALUES (714, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1004802928', '<em>华为</em> <em>matebook14</em> <em>2020款</em> 触摸屏对于大学生来说用处大吗?', NULL, '实际上类似于<em>MateBook</em> <em>14</em> <em>2020款</em>这样的非二合一电脑的触摸屏实际用处很小，可能在平时需要点按鼠标又不方便的时候能够点屏幕会方便一些，但是在正常使用这款笔记本的时候');
INSERT INTO `commodity_review_info` VALUES (715, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1066495162', '<em>华为matebook</em>142020<em>款</em>咋样?', NULL, '值得购买，对于尤其是文字工作者来说，<em>MateBook</em> <em>14</em>相当适合。 MateBook 13/14的2020版都是在2020年2月更新的，所以显而易见的不足之处就是起码在半年内不会更新MX350显卡');
INSERT INTO `commodity_review_info` VALUES (716, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1076907290', '<em>HUAWEI</em> <em>matebook14</em> <em>2020款</em> 独显 i5 16GB 2K触控屏6399值得入手吗？', NULL, '算是升值吧。 但是如果没有华为手机的话，其实就没有完全发挥出它的作用了。但是2k屏这点真的还是超级棒的。 个人用的是matebook14 2019的i5款，感觉平时使用都是完全足够了的');
INSERT INTO `commodity_review_info` VALUES (717, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1036735038', '如何评价 2 月 24 日发布的 <em>MateBook</em> X Pro <em>2020</em> <em>款</em>？', NULL, '专门放在线下店坑小白。 前面的<em>Matebook</em> 13/<em>14</em>，到现在也是，换了一个触摸屏，就叫<em>2020款</em>了。 现在的<em>Matebook</em> X Pro也是如此。 可以怀疑，<em>华为</em>的笔记本现在是不舍得投入成本开新的模具');
INSERT INTO `commodity_review_info` VALUES (718, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1026399741', '<em>2020款华为matebook</em> <em>14</em> 有什么槽点？', NULL, '推荐看我的实际使用报告： https://zhuanlan.zhihu.com/p/107562757');
INSERT INTO `commodity_review_info` VALUES (719, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1007275154', '<em>华为MateBook</em> 13/<em>14</em> <em>2020款</em>的实际体验如何？', NULL, '可以参考去年5月棉花发布的2019<em>款MateBook</em> <em>14</em>评测：cotton：再度发力中端轻薄本——<em>HUAWEI</em> <em>MateBook</em> <em>14</em>详细评测 由于疫情较为严重，短时间内棉花无法返校访问存储在学校台式机上的上代MateBook');
INSERT INTO `commodity_review_info` VALUES (720, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1039905995', '<em>华为matebook</em> x pro 2019与新款的<em>matebook</em> <em>14</em> <em>2020</em>哪一个更值得购买？', NULL, '原因就是16G的内存和十代酷睿处理器。 MateBook X Pro作为MateBook的最高端机型之前一直讨论不多，现在<em>MateBook</em> <em>14</em>更新了2020款之后我们也可以把这两款产品来进行一番比较了');
INSERT INTO `commodity_review_info` VALUES (721, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1071463032', '现在入手<em>matebook</em> <em>14</em> <em>2020款</em>还是等小新pro 13 <em>2020款</em>？', NULL, '主流网游两款电脑都无压力。 三、<em>MateBook</em> <em>14</em>的优势——颜值、键盘、接口 1、颜值——<em>MateBook</em> <em>14</em>的确更高一些 由于<em>MateBook</em> <em>14</em>用了更极致的3');
INSERT INTO `commodity_review_info` VALUES (722, '笔记本电脑', '华为', 'HUAWEI MateBook 14 2020款', 'https://www.zhihu.com/answer/1141015707', '2 月 24 日发布的<em>华为</em> <em>MateBook</em> X Pro <em>2020</em> <em>款</em>有哪些亮点和不足？', NULL, '我个人比较关注的还是笔记本电脑方面，这次的<em>华为MateBook</em> X Pro <em>2020</em>基于目前评测所得到的数据来讲，可以选择两台： <em>华为MateBook</em> X Pro核心显卡版本 华为MateBook');
INSERT INTO `commodity_review_info` VALUES (723, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1036735038', '如何评价 2 月 24 日发布的 <em>MateBook</em> <em>X</em> <em>Pro</em> <em>2020</em> <em>款</em>？', NULL, '专门放在线下店坑小白。 前面的<em>Matebook</em> 13/14，到现在也是，换了一个触摸屏，就叫<em>2020款</em>了。 现在的<em>Matebook</em> <em>X</em> <em>Pro</em>也是如此。 可以怀疑，<em>华为</em>的笔记本现在是不舍得投入成本开新的模具');
INSERT INTO `commodity_review_info` VALUES (724, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1134647684', '最新发布的<em>华为MateBook</em> <em>X</em> <em>Pro</em>实际体验如何？', NULL, '我对于全面屏的笔记本没什么概念，毕竟pc和手机不一样，不是每一个厂商都跟进全面屏的，而<em>华为MateBook</em> <em>X</em> <em>Pro</em> <em>2020款</em>着实给了我一个大惊喜，四面窄边框的全面屏真的十分震撼，屏占比91%');
INSERT INTO `commodity_review_info` VALUES (725, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/974114629', '<em>2020</em>年入手<em>华为</em>mate20Pro大家觉得怎么样？', NULL, '买之前在mate20Pro和一加7<em>Pro</em>之间纠结了好久。但是感觉一加7<em>Pro</em>除了在屏幕上领先<em>华为</em>，其他真的没感觉。而且自己一直是用的<em>华为</em>。不过一加7<em>Pro</em>的升降式摄像头的全面屏是真心好看啊');
INSERT INTO `commodity_review_info` VALUES (726, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1039905995', '<em>华为matebook</em> <em>x</em> <em>pro</em> 2019与新款的<em>matebook</em> 14 <em>2020</em>哪一个更值得购买？', NULL, '原因就是16G的内存和十代酷睿处理器。 <em>MateBook</em> <em>X</em> <em>Pro</em>作为<em>MateBook</em>的最高端机型之前一直讨论不多，现在<em>MateBook</em> 14更新了<em>2020款</em>之后我们也可以把这两<em>款</em>产品来进行一番比较了');
INSERT INTO `commodity_review_info` VALUES (727, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1141015707', '2 月 24 日发布的<em>华为</em> <em>MateBook</em> <em>X</em> <em>Pro</em> <em>2020</em> <em>款</em>有哪些亮点和不足？', NULL, '这次的华为MateBook <em>X</em> <em>Pro</em> <em>2020</em>基于目前评测所得到的数据来讲，可以选择两台： <em>华为MateBook</em> <em>X</em> <em>Pro</em>核心显卡版本 <em>华为MateBook</em> <em>X</em> <em>Pro</em> 翡冷翠版本');
INSERT INTO `commodity_review_info` VALUES (728, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1007275154', '<em>华为MateBook</em> 13/14 <em>2020款</em>的实际体验如何？', NULL, '实际上带来了很好的移动办公体验。<em>MateBook</em> 14 <em>2020款</em>的触摸屏和<em>Matebook</em> <em>X</em> <em>Pro</em>一样支持十点触控，而<em>Matebook</em> <em>X</em> <em>Pro</em>上拥有的三指截屏等功能也一并被下放到MateBook');
INSERT INTO `commodity_review_info` VALUES (729, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/993914410', '如何评价<em>华为</em>推出全新的 <em>MateBook</em> 13/14 <em>2020</em> <em>款</em>？值得入手吗？', NULL, '不展开细说了。 题外话：我依然钟爱之前 <em>MateBook</em> Xpro 的菊花，而不是现在的“<em>HUAWEI</em>” 硬件 <em>MateBook</em> 13/14 性能 内存：16G（升级到大内存）');
INSERT INTO `commodity_review_info` VALUES (730, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1144225201', '华为<em>matebook</em> <em>x</em> <em>pro</em> <em>2020</em> 和小新 <em>pro</em>13 <em>2020</em>，附想买的图片，求大佬推荐？', NULL, '你这个预算为什么不买苹果呢。性能可以吊打你说的两款。虽然我是<em>华为</em>粉，但是<em>matebook</em>系列高端机还是谨慎买吧……小新我是更不推荐了，千万别入坑。');
INSERT INTO `commodity_review_info` VALUES (731, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1054029073', '<em>华为matebook</em> D14 <em>2020款</em>和D15 <em>2020款</em>以及<em>X</em> <em>pro2020款</em>哪个好？', NULL, '先说结论：<em>X</em> <em>Pro2020款</em>是最好的，是<em>华为</em>旗舰笔记本。 然后理清一下<em>华为</em>笔记本产品线。 二合一上网本：<em>华为Matebook</em> E系列 低端轻薄本：华为Matebook');
INSERT INTO `commodity_review_info` VALUES (732, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1004802928', '<em>华为</em> <em>matebook</em>14 <em>2020款</em> 触摸屏对于大学生来说用处大吗?', NULL, '实际上类似于<em>MateBook</em> 14 <em>2020款</em>这样的非二合一电脑的触摸屏实际用处很小，可能在平时需要点按鼠标又不方便的时候能够点屏幕会方便一些，但是在正常使用这款笔记本的时候');
INSERT INTO `commodity_review_info` VALUES (733, '笔记本电脑', '华为', 'HUAWEI MateBook X Pro 2020款 ', 'https://www.zhihu.com/answer/1071463032', '现在入手<em>matebook</em> 14 <em>2020款</em>还是等小新<em>pro</em> 13 <em>2020款</em>？', NULL, '首先说明一下目前已经小新<em>Pro</em>13即将更新到<em>2020</em>版，升级点说白了就是将显卡更新到MX350，其他地方维持不变，锐龙版还需要继续等，产能还跟不上。所以<em>Pro</em>13 <em>2020</em>版只是将原先的性能优势进一步拉大了');
INSERT INTO `commodity_review_info` VALUES (734, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/555901786', '华为matebook<em>13</em>怎么样?', NULL, '烦请点个赞同，评论数远高于赞同数了，非常感谢！） 更新回答： 2020款<em>MateBook13</em>和14发布之后，这两款笔记本依旧是七千元以下“屏价比”最高的两款笔记本');
INSERT INTO `commodity_review_info` VALUES (735, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/553864218', '如何评价<em>华为</em> <em>MateBook</em> <em>13</em>？', NULL, '所以有点后悔没有买x pro。 最近不断或者点赞决定再跟新一波关于<em>matebook13</em>打游戏的。我是用<em>matebook13</em> 外接1080显示器打LOL，使用的默认是中高级画质');
INSERT INTO `commodity_review_info` VALUES (736, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/993914410', '如何评价<em>华为</em>推出全新的 <em>MateBook</em> <em>13</em>/14 2020 款？值得入手吗？', NULL, '不展开细说了。 题外话：我依然钟爱之前 <em>MateBook</em> Xpro 的菊花，而不是现在的“<em>HUAWEI</em>” 硬件 <em>MateBook</em> <em>13</em>/14 性能 内存：16G（升级到大内存）');
INSERT INTO `commodity_review_info` VALUES (737, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/895491799', '3899 元的<em>华为</em> <em>MateBook</em> <em>13</em> 锐龙版的实际体验如何？值得购买吗？', NULL, '感觉屏幕太大了（出差以及日常处理文档偏爱<em>13</em>寸），机器颜值一般。虽然配置刚好，价格也便宜，心里还是犹豫的。 最后选定<em>华为matebook</em> <em>13</em>是刚好家里附近新开了一家<em>华为</em>体验店，在店里体验了一把');
INSERT INTO `commodity_review_info` VALUES (738, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/1092074536', '2020版本<em>华为matebook</em> <em>13</em>和14哪个更值得入手?', NULL, '不是我把它pass了，是它的价格把我pass了。 鼠标问题:旗舰店写评论送了个<em>华为</em>鼠标，<em>华为</em>鼠标是直接跟<em>13</em>的内置蓝牙连的，不需要接收器。 键盘问题:体验很差，基本相当于敲钢板');
INSERT INTO `commodity_review_info` VALUES (739, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/750873546', '请问华为matebook<em>13</em>值得入手吗?', NULL, '前两天刚刚买的<em>matebook13</em>，来回答一下。 电脑很迷你，只有一本教科书那么大可以用手拎着到处走。打开之后屏幕很好，清晰。我设置的王一博动态屏幕十分养眼。 缺点确实是发热问题、电池问题和接口少');
INSERT INTO `commodity_review_info` VALUES (740, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/670021124', '现在买华为matebook<em>13</em>和<em>matebook</em>14更推荐哪一款呀？或者其他同价位的笔记本？', NULL, '我也聊天中知道有些朋友在<em>13</em>寸上也用100%缩放，人家一点不觉得小。所以说看使用习惯了…… 性能上，<em>华为</em>mtb<em>13</em>和14上的调教都比较积极，但是考虑到温度的话，mtb<em>13</em>还好，mtb14的左手掌托热炸了……性能参数好看了');
INSERT INTO `commodity_review_info` VALUES (741, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/849591834', '<em>matebook13</em>和14怎么选择？', NULL, '我买了<em>matebook13</em> i5第三方，我旁边寝室的妹子买了<em>matebook</em>14 i5，首先从外观上来说的话我不太喜欢<em>huawei</em>的英文字母，所以这里我选择了logo');
INSERT INTO `commodity_review_info` VALUES (742, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/1005243332', '<em>华为MateBook</em> <em>13</em>/14 2020款的实际体验如何？', NULL, '女生：i5/16G/512G <em>MateBook</em> <em>13</em> 男生：i5/16G/512G <em>MateBook</em> 14 <em>MateBook</em> <em>13</em> 樱粉金10号开售哦。真的好好看');
INSERT INTO `commodity_review_info` VALUES (743, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/969012395', '华为MateBook<em>13</em>锐龙版续航怎么样？', NULL, '<em>HUAWEI MateBook 13</em>锐龙版，售价4099元 华为MateBook 13锐龙版是华为专门满足学生和职场新人需求而推出的，主要特点是2K高清全面屏、13英寸高颜值轻薄便携、AMD处理器强劲性能、以及一碰传、多屏协同等交互功能');
INSERT INTO `commodity_review_info` VALUES (744, '笔记本电脑', '华为', 'HUAWEI MateBook 13', 'https://www.zhihu.com/answer/982174155', '<em>matebook</em> <em>13</em> 和小新Pro<em>13</em> 该怎么选啊？', NULL, '我桌上放着小新Pro <em>13</em> <em>Matebook</em> <em>13</em>除了颜值上略高一丢丢之外，在其他方面可以说被小新Pro <em>13</em>碾压。 参见：联想小新pro <em>13</em>，和<em>华为matebook</em> 13，选哪一个好');
INSERT INTO `commodity_review_info` VALUES (745, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/993914410', '如何评价<em>华为</em>推出全新的 <em>MateBook</em> <em>13</em>/14 <em>2020</em> <em>款</em>？值得入手吗？', NULL, '成为全新的“真香本”？ <em>MateBook</em> <em>13</em> 即13英寸轻薄本；MateBook 14 即14英寸轻薄本。 前情提要，<em>MateBook</em> <em>13</em>/14 2020 主要升级了以下内容');
INSERT INTO `commodity_review_info` VALUES (746, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1005243332', '<em>华为MateBook</em> <em>13</em>/14 <em>2020款</em>的实际体验如何？', NULL, '更应该炫耀的，是科技如何“为人所用”。 从这次的宣传海报上来看，华为全新 <em>MateBook</em> <em>13</em>/14 2020款主打三大卖点： 2K「触控」全面屏 「多屏协同」 16GB「大内存」');
INSERT INTO `commodity_review_info` VALUES (747, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1047292249', '<em>HUAWEI</em> <em>matebook</em>14 <em>2020款</em>值得入手吗？', NULL, '用着还不错。------<em>2020</em>.4.11------2月10号到手的，一直用的很仔细，刚到手一周就开始掉漆，现在边缘部分涂层手指甲一碰到就掉漆，这种情况大家都遇到过么？');
INSERT INTO `commodity_review_info` VALUES (748, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1036735038', '如何评价 2 月 24 日发布的 <em>MateBook</em> X Pro <em>2020</em> <em>款</em>？', NULL, '专门放在线下店坑小白。 前面的<em>Matebook</em> <em>13</em>/14，到现在也是，换了一个触摸屏，就叫<em>2020款</em>了。 现在的<em>Matebook</em> X Pro也是如此。 可以怀疑，<em>华为</em>的笔记本现在是不舍得投入成本开新的模具');
INSERT INTO `commodity_review_info` VALUES (749, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1092074536', '<em>2020</em>版本<em>华为matebook</em> <em>13</em>和14哪个更值得入手?', NULL, '不是我把它pass了，是它的价格把我pass了。 鼠标问题:旗舰店写评论送了个华为鼠标，华为鼠标是直接跟13的内置蓝牙连的，不需要接收器。 键盘问题:体验很差，基本相当于敲钢板');
INSERT INTO `commodity_review_info` VALUES (750, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1004991168', '<em>2020款</em>华为matebook<em>13</em>和14哪个更好，有什么区别？', NULL, '前置摄像头拍摄角度会正常好多。整体感觉matebook13适合女生使用，特别是有粉色款可以选。而且...matebook13价格没记错的话要便宜一些吧... matebook14相对于matebook13屏幕稍微大一些');
INSERT INTO `commodity_review_info` VALUES (751, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1076907290', '<em>HUAWEI</em> <em>matebook</em>14 <em>2020款</em> 独显 i5 16GB 2K触控屏6399值得入手吗？', NULL, '轻薄本个人感觉…就买一个颜值。好看就上呗 我感觉<em>matebook</em>14 <em>2020款</em>相对于原本的2019提升超大...再同价位的轻薄本中，还推荐小新pro<em>13</em>或者yoga s470，这两<em>款</em>的性价比也都是超高的。如果想要价格低一点的话可以考虑一些同款的AMD系列');
INSERT INTO `commodity_review_info` VALUES (752, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/998000611', '华为matebook14 <em>2020</em>版出了，还有必要买小新pro<em>13</em>吗？', NULL, '比指纹要少一个步骤，显然更方便一些。 6、额外功能——MateBook 14里面华为Share配合华为手机更好 华为Share算是除了苹果之外体验最好的PC与手机的联动功能');
INSERT INTO `commodity_review_info` VALUES (753, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/998790575', '<em>华为</em> <em>MateBook</em> <em>13</em>/14 <em>2020</em> <em>款</em>和联想小新 Pro<em>13</em>/Air 14 相比，选哪个？', NULL, '轻薄外观且有粉色可以选择加华为多屏生态构建这都是其优势。（屏幕也支持触控） 详细一些的评测可以参考：如何评价华为推出全新的 <em>MateBook</em> <em>13</em>/14 <em>2020</em> 款？值得入手吗');
INSERT INTO `commodity_review_info` VALUES (754, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1071310988', '刚买的<em>matebook13</em>要不要退了换<em>2020</em>新款?', NULL, '时间已经到了2020年了，华为已经将<em>MateBook</em> <em>13</em>更新到了2020版，<em>MateBook</em> <em>13</em>也因此更新了配置，赶上了最新的潮流，如今的<em>MateBook</em> <em>13</em> <em>2020</em>版现在标配十代酷睿处理器、16G内存、512G固态以及触摸屏幕');
INSERT INTO `commodity_review_info` VALUES (755, '笔记本电脑', '华为', 'HUAWEI MateBook 13 2020款', 'https://www.zhihu.com/answer/1071463032', '现在入手<em>matebook</em> 14 <em>2020款</em>还是等小新pro <em>13</em> <em>2020款</em>？', NULL, '首先说明一下目前已经小新Pro13即将更新到2020版，升级点说白了就是将显卡更新到MX350，其他地方维持不变，锐龙版还需要继续等，产能还跟不上。所以Pro<em>13</em> <em>2020</em>版只是将原先的性能优势进一步拉大了');
INSERT INTO `commodity_review_info` VALUES (756, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/720515917', '“<em>神舟</em>(HASEE)<em>战神Z7</em>-<em>CT5NA</em> ”这款电脑怎么样？', NULL, '目前已购买，感觉没啥问题，跑分32万多。现在还在测试，感觉也没有那么容易翻船。上几张图关于我自己测试的数据。 今天下了一个刺客信条——起源，然后玩了一会感觉画面');
INSERT INTO `commodity_review_info` VALUES (757, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/1077096605', '<em>神舟战神z7</em>-<em>CT5NA</em>值得买吗？', NULL, '6599，，， z7ct7nt双十一pdd也是这价，而且z7ct7nt比这要强');
INSERT INTO `commodity_review_info` VALUES (758, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/1003341395', '神州<em>z7</em>-<em>ct5na</em>和拯救者y7000p哪个好？', NULL, '一更 也是最后一更 如果你是电脑品牌无脑吹，我祝你吹的开心，如果你是追求真正好的机器，那我也祝你用的舒心。 另外无脑吹也请别评论我了，谢谢。 以下原答案 电子产');
INSERT INTO `commodity_review_info` VALUES (759, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/709020463', '请问这家的<em>神舟Z7</em>-<em>CT5NA</em>现在可以入手吗？', NULL, '居然刷到我的店铺了。 哈哈，看看这个评测就知道了。 神船大副：<em>神舟Z7</em>-<em>CT5NA</em>评测 价格最低的GTX1660Ti游戏本');
INSERT INTO `commodity_review_info` VALUES (760, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/670961241', '<em>神舟</em>为什么配置这么高，价格缺让人心慌？', NULL, '给的图应该是<em>Z7</em>-<em>CT5NA</em>。这批使用的模具是NH5X……算是比较低端的了。高色域或者高刷新率都naidesu。自然价位会比较低。 如果不在意你眼睛的体验、可能比较渣渣的散热和抽奖固态的话可以考虑购买');
INSERT INTO `commodity_review_info` VALUES (761, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/734508262', '<em>神舟</em>笔<em>战神Z7</em>-<em>CT5NA</em>值得推荐买吗？', NULL, '本人也是一个大学生，在选择笔记本的时候也是特别的犹豫，最后还是购买了这款笔记本。本人也曾测评过该产品，详情可以参照我的另一个回答。<em>神舟战神Z7-CT5NA</em>测评');
INSERT INTO `commodity_review_info` VALUES (762, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/795414880', '有哪些是你用了游戏笔记本才明白的事情？', NULL, '买的<em>神舟战神Z7-CT5NA</em>，屏幕当时没注意，买回来才知道是低色域（现在笔记本几乎都没有低色域了吧）。 风扇声音巨大。玩LOL时声音就偏大，玩吃鸡时声音就大得令人发指');
INSERT INTO `commodity_review_info` VALUES (763, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/693097086', '<em>神舟战神ct5na</em>，i59300h 1660ti。怎么样划算吗，质量如何。？', NULL, '大副这里评测了I7的，你可以看看I7的评测，他们差别就是CPU的差异，其他一样：神船大副：神舟Z7-CT7NA评测实测 GTX1660TI游戏本实测 外观霸气配置合理性价比屌炸天');
INSERT INTO `commodity_review_info` VALUES (764, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/820693642', '<em>神舟战神Z7</em>-<em>ct5na</em>色域多少，自己怎么换？', NULL, '屏幕很好换，机子键盘取下来，键盘那面取下来，把屏幕接口拔下来，然后去网上买一块相同大小接口一样的屏幕换上就行了。屏幕边框是卡扣，用银行卡之类的先划拉一圈就行啦。');
INSERT INTO `commodity_review_info` VALUES (765, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/713597819', '<em>神舟战神Z7</em>-CT7NA性价比怎么样？会翻船吗？', NULL, '吹爆z7，快买吧。物超所值众望所归。 这些问题真的没有必要再问了。以前知友的各种回答建立的庞大的信息体系足够回答这个问题了，稍微有点学习能力的人都知道买哪个。 一个问题');
INSERT INTO `commodity_review_info` VALUES (766, '笔记本电脑', '神舟', '神舟战神Z7-CT5NA', 'https://www.zhihu.com/answer/589420259', '如何评价<em>神舟z7</em>系列？', NULL, 'GX是超级<em>战神</em>的17.3英寸桌面级处理器游戏本 ZX是超级<em>战神</em>的15.6英寸桌面级处理器游戏本 <em>Z7</em>是<em>神舟战神</em>系列的移动端处理器1060游戏本，经过数代的模具缩水');
INSERT INTO `commodity_review_info` VALUES (767, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/734508262', '<em>神舟</em>笔<em>战神</em>Z7-<em>CT5NA</em>值得推荐买吗？', NULL, '本人也是一个大学生，在选择笔记本的时候也是特别的犹豫，最后还是购买了这款笔记本。本人也曾测评过该产品，详情可以参照我的另一个回答。<em>神舟战神</em>Z7-<em>CT5NA</em>测评');
INSERT INTO `commodity_review_info` VALUES (768, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/771643275', '<em>神舟战神Z7M-CT5NA</em>电脑i5--9300h，gtx1650独显，8g，512固态，可以吃鸡吗？', NULL, '可以吃鸡的哈，特效水平中高，建议再加8g内存');
INSERT INTO `commodity_review_info` VALUES (769, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/870975092', '想入手台游戏本，预算6000上下，相中几款：<em>神舟战神z7m</em>-<em>CT5NA</em>，联想拯救者Y7000选哪个？', NULL, '把手放上面(至少手腕部位手指部位要一直在上面)能忍就买，不能就别买 神舟c7最大的优点就是并没有像上面这些型号这么明显的缺点。 对，他的优点是没有特别的明显缺点，还有便宜 首先你要知道神舟不是慈善机构，他也要赚钱的');
INSERT INTO `commodity_review_info` VALUES (770, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/1007660100', '“<em>神舟</em>(HASEE)<em>战神</em>Z7-<em>CT5NA</em> ”这款电脑怎么样？', NULL, '这款电脑可以认为是<em>Z7m</em>的高配款也可以认为是Z7的低配款，对前者是显卡上的升级，对后者则是U上的降级；但作为一台游戏本来说这款产品确实是一个折中且不错的选择。 该产品的定位十分明确');
INSERT INTO `commodity_review_info` VALUES (771, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/828348115', '这个笔记本怎么样？<em>神舟Z7M</em>-<em>CT5NA</em>？', NULL, '谢邀，用了几个月的CT7NA 最直白的区别如名字，一个i5,一个i7 如题，吃鸡守望lol 本人不玩守望，对吃鸡lol还是有发言权的，这个型号的模具对散热这一块');
INSERT INTO `commodity_review_info` VALUES (772, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/828463431', '已经上了<em>Z7M</em>-<em>CT5NA</em>船，有什么要注意的吗？', NULL, '别上百度看那群喷子的表演，身边有懂电脑的找他帮你重装一下系统，然后上官网下驱动注意散热，最好买个散热板');
INSERT INTO `commodity_review_info` VALUES (773, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/740700695', '<em>神舟</em>游戏本真的值得买吗？', NULL, '我个人亲身经历就是几年前帮同事买了一台<em>战神</em>K650D（应该是这个型号，已经不是很清楚了）...执意交 智商税 在京东<em>神舟</em>自营店买了一台<em>神舟战神</em>的<em>Z7M</em>-<em>CT5NA</em>， 7月18号发货');
INSERT INTO `commodity_review_info` VALUES (774, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/746478207', '<em>神舟战神</em>Z7-CT7NA 和<em>神舟战神Z7M</em>-CT7NA差在哪里？', NULL, '固态好像也不一样。z7是nvme的，z7m是sata的，详情见下面两个链接：【图片】战神Z7-CT7NA加装内存和机械硬盘20190623【神舟笔记本吧】_百度贴吧【图片】刚京东买的z7m－CT7NA…各位大神帮忙看下…有翻船吗');
INSERT INTO `commodity_review_info` VALUES (775, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/923549970', '<em>神舟战神z7m</em>-<em>ct5na</em>与华硕飞行堡垒7哪个好点？', NULL, '不是在这里吹捧呀什么的。<em>神舟战神Z7M-CT5NA</em>和华硕飞行堡垒7(R7-3750H/8GB/512GB/GTX1050)有什么区别_<em>神舟战神Z7M-CT5NA</em>和华硕飞行堡');
INSERT INTO `commodity_review_info` VALUES (776, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/849029037', '<em>战神z7m</em>-ct7na好不好？', NULL, '谢邀，本人一直在用 详情移步我的回答，说得比较细 https://www.zhihu.com/question/341964904/answer/7993038');
INSERT INTO `commodity_review_info` VALUES (777, '笔记本电脑', '神舟', '神舟战神Z7M-CT5NA', 'https://www.zhihu.com/answer/974712572', '2019年，你都买了哪台笔记本电脑？使用体验怎么样？', NULL, '<em>神舟z7m</em> <em>ct5na</em> 用券后4986 1650的显卡 i5+9300h 512的固态 8g内存 只想说真香 刺客信条 gta5高特效运行 从买来后没有任何毛病');
INSERT INTO `commodity_review_info` VALUES (778, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/168594746', '请问<em>战神K670D</em>-<em>G4D1</em>值得买吗？京东报价4499？', NULL, '今天（2017.5.12）下午五点，下单，<em>战神K670D</em>-<em>G4D1</em>，淘宝。预计下周一到货。 也犹豫了一段时间，毕竟<em>神舟</em>电脑名声一直不太好，后来形成以下想法： 1、如果品质真的像传闻中那样不堪');
INSERT INTO `commodity_review_info` VALUES (779, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/337588948', '神州电脑<em>战神</em>k670 <em>g4D1</em>怎么样？', NULL, '这个处理器玩一般游戏没问题。小毛病一般集中在win10上，硬件有问题的，大多数是漏光，风扇声音大这种。多用几个同类型的笔记本就知道这是不是问题了。');
INSERT INTO `commodity_review_info` VALUES (780, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/156511618', '<em>神舟战神</em>K680D-<em>G4D1</em>升级意见？', NULL, 'cpu一直在80以上一定是因为你浏览器用的flash播放器，比如斗鱼优酷土豆爱奇艺之类的，b站默认的也是flash。 b站的播放器是可以替换成html5播放器的');
INSERT INTO `commodity_review_info` VALUES (781, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/564545439', '<em>神舟</em>笔记本真的像网上说的那么差那么不靠谱吗？', NULL, '这是一个概率问题。 <em>神舟k670d</em>－<em>g4d1</em>使用中。 购买原因，便宜。 1050才4300，不买亏了(2017年6月买的)。 现在。。。。这尼玛就是个吞金巨兽。');
INSERT INTO `commodity_review_info` VALUES (782, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/1157761531', '<em>神舟战神K670d</em>-<em>G4D1</em>支不支持加pcie协议的nvme固态？', NULL, '2固态就完事了，貌似还可以上2个，那就是双倍的快乐。 打扰了，告辞 我刚电话咨询了神舟，她们说是m.2,sata的，我具体说总线，协议，反正她不懂，然后就这样了，下面的回答说是支持nvme协议的');
INSERT INTO `commodity_review_info` VALUES (783, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/349860948', '<em>神舟</em> <em>战神</em> <em>K670D</em>-<em>G4D1</em> 1050笔记本电脑怎么样，求解说。性价比怎么样。在这个价位。？', NULL, '总觉得船是不好上的，我一同学，很懂电脑的，买了<em>神舟</em>z8，动不动就CPU飙到99度显卡90度。还曾自己换过mx4的硅脂，开强冷都不行。 当然其它得<em>神舟</em>不一定会这样。');
INSERT INTO `commodity_review_info` VALUES (784, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/669050550', '如何评价<em>神舟战神</em>的笔记本？', NULL, 'com/s/1bgcy3删0BTzg8iHYrcP删WncDw 提取码:7nw2 前年12月份在淘宝买的K690E-G4D1 配置其实挺不错的 G4600＋1060 6GB＋1TB机械硬盘＋128GB（m2）＋RGB键盘＋45%色域ips屏');
INSERT INTO `commodity_review_info` VALUES (785, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/302184160', '<em>神舟战神K670D-G4D1</em>升级CPU-i5 7500？', NULL, '图是k680e的配置参数 但是w650kk(k680）和w650kj(k670)只是显卡不同而已 主板是H170 直接换就是 可惜下代估计看不到w650系列了 ');
INSERT INTO `commodity_review_info` VALUES (786, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/221965119', '求推荐一款笔记本啊，五千以内的预算？', NULL, '<em>神舟K670D</em>-<em>G4D1</em>，G4560/8G/128G/1TB/ GTX1050 4G，性价比没谁了，一般4K视频剪辑都不大有问题的性能基础，游戏嘛，G4560性能接近笔记本i5标压没毛病');
INSERT INTO `commodity_review_info` VALUES (787, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/378562524', '请问<em>神舟战神</em>哪款性价比高？看上的款式<em>战神</em>X5-CP7 S1 和<em>k670D</em> -<em>G4D1</em>或推荐其他款式？', NULL, '最好别买二线本...这个售后是真的恶心。而且经常翻船，不如你就加点钱上微星 华硕好了');
INSERT INTO `commodity_review_info` VALUES (788, '笔记本电脑', '神舟', '神舟战神K670D-G4D1', 'https://www.zhihu.com/answer/399909451', '<em>战神k670d</em>–<em>g4d1</em>能够换好一点的cpu吗？有什么推荐？', NULL, '大致就这些。看你喜欢');
INSERT INTO `commodity_review_info` VALUES (789, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/853424331', '<em>神舟z7m</em>—ct7gs怎么样？', NULL, '性价比最高的中度及以下打游戏，兼顾办公移动的轻薄笔记本，6000已经是不错的价，如果能开车拿到5399，那就无敌了。但是记得加一块pm981或者sn750做系统');
INSERT INTO `commodity_review_info` VALUES (790, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/821464994', '为什么没有<em>神舟Z7M</em>-CT7GS评测？', NULL, '单纯从问题角度回答的话，是因为<em>神舟</em>把一个模具贴出来了很多型号……没有媒体闲的空会去一个一个测过来。 如果你想知道这机器的优劣，那我大概讲一下： 优点是性价比非常高');
INSERT INTO `commodity_review_info` VALUES (791, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/1019487659', '<em>神舟</em>笔记本硬件质量如何。?', NULL, '现在就在用<em>神舟战神Z7M-CT7GS</em>这款，用了快两个多月了，之前买的时候想买华硕或者微星，但是苦于资金不太够，就在机械革命和神舟之间纠结了。你知道的，同等配置的前提下都想买一个最合算的');
INSERT INTO `commodity_review_info` VALUES (792, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/933307474', '想问问各位大神<em>神舟战神Z7M-CT7GS</em>怎么样？', NULL, '除了没有机械硬盘这一块，如果数据不多，固态512完全够用，后期可以加装，这台笔记本有一个2.5寸SATA3.0接口的硬盘位，可以自己加装一个2.5寸SATA3.');
INSERT INTO `commodity_review_info` VALUES (793, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/804988753', '6000左右预算，学生党，看剧玩游戏（英雄联盟，天涯明月刀等）剪辑，有什么好的笔记本推荐吗？屏幕好点？', NULL, '主要配置：（i7-9750H 8G 512G SSD GTX1650） <em>神舟</em>(HASEE)<em>战神Z7M</em>-CT7NA 15.6英寸游戏笔记本电脑 主要配置：英特尔酷睿i7-9750H');
INSERT INTO `commodity_review_info` VALUES (794, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/774092793', '<em>神舟战神</em> <em>Z7M</em>-CT7GS和光影精灵5，不玩游戏的话哪个好?', NULL, '没可比性 神舟翻船几率大，售后还特别差。战神是游戏本，不玩游戏买游戏本干啥？ 惠普产品自然质量没的说。光影精灵虽然也是游戏本，但是外观非常偏向于商务类型。不玩游戏正好');
INSERT INTO `commodity_review_info` VALUES (795, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/807914653', '<em>神舟z7m</em>-<em>ct7gs</em>和<em>神舟</em>z7-mct7na哪个散热好一点？', NULL, '只摸过朋友的NA。 GS是轻薄游戏本，散热有牺牲。 但如果是学生党我仍然推荐<em>Z7M</em>-CT7GS，在图书馆来回跑的你就会发现，习武真的很累。');
INSERT INTO `commodity_review_info` VALUES (796, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/807150892', '机械革命z2air和<em>神舟战神z7m</em>－ct7gs选哪个好点？', NULL, '我的是z7mct7gs,感觉还不错');
INSERT INTO `commodity_review_info` VALUES (797, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/1065085029', '<em>神舟Z7M</em>-CT7GS这个电脑怎么样？', NULL, '想不开了？选神州，你不如选个联想拯救者y7000，香的一批');
INSERT INTO `commodity_review_info` VALUES (798, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/746478207', '<em>神舟战神</em>Z7-CT7NA 和<em>神舟战神Z7M</em>-CT7NA差在哪里？', NULL, '固态好像也不一样。z7是nvme的，z7m是sata的，详情见下面两个链接：【图片】战神Z7-CT7NA加装内存和机械硬盘20190623【神舟笔记本吧】_百度贴吧【图片】刚京东买的z7m－CT7NA…各位大神帮忙看下…有翻船吗');
INSERT INTO `commodity_review_info` VALUES (799, '笔记本电脑', '神舟', '神舟战神Z7M-CT7GS', 'https://www.zhihu.com/answer/366664065', '<em>神舟战神Z7M</em>值得买么？', NULL, '这是官方旗舰店 弄个链接吧 上午的 【我剁手都要买的宝贝（Hasee/<em>神舟</em> <em>战神</em> <em>Z7M</em>-KP7GC 八代i7吃鸡1050Ti游戏本笔记本电脑），快来和我一起瓜分红I包】');
INSERT INTO `commodity_review_info` VALUES (800, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/953690487', '今年双十一3800买了神州<em>战神k670c</em>-g6a1，诸位大神感觉怎么样，划算吗？', NULL, '总感觉这样选高U低显有点奇怪 →_→ 为何不选G5420+GTX1050的配置后期自行升级到9400⚆_⚆？');
INSERT INTO `commodity_review_info` VALUES (801, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/1052427804', '<em>神舟战神k670c</em> -G6A1 怎么样?', NULL, '战神家的产品有高性价比之称，但是售后很不好，邀约一次售后服务要跑五里地，亲亲可以看看戴尔的g3还是很不错的。');
INSERT INTO `commodity_review_info` VALUES (802, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/1095574637', '请问4k以内<em>神舟</em>哪一款笔记本打游戏性能好一些 或者有其他笔记本推荐嘛。?', NULL, '远程办公 或者试一下非自营的神船，i5-9400配mx250，8G内存512固态：<em>神舟战神</em>K670D/K680E GTX1050/1050Ti独显吃鸡15.6英寸手提游戏笔记本电脑');
INSERT INTO `commodity_review_info` VALUES (803, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/476455775', '用过<em>神舟战神</em>k670的前辈们感觉还可以吗，今年想买来耍一耍的，想征求一下意见？', NULL, '什么K670？ 14年的K670D还是K670G 还是18年K670D/E 分别搜P150/151/155模具，或者W650KK/KL模具就好了。');
INSERT INTO `commodity_review_info` VALUES (804, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/1101867530', '学机械的3D建模不打游戏，有什么性价比高的电脑推荐，跪谢？', NULL, '办公也会舒服许多。 3.重量要适中，携带要轻便。 推荐如下几台笔记本。 1.<em>神舟</em> <em>战神</em> K670C-G6A1 如果你的预算不是很多，5K以内，推荐这一台笔记本，虽然这一台屏幕不怎么样');
INSERT INTO `commodity_review_info` VALUES (805, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/60635454', '<em>神舟</em>笔记本靠谱不？', NULL, '那么你在这个问题里面看到的各种答案把<em>神舟</em>的性价比甚至是做工等方方面面都说的再高，也不要买。 关于做工问题，我个人的观点是<em>神舟</em>高端的笔记本（特指<em>战神</em>系列）做工并不比联想之流的差');
INSERT INTO `commodity_review_info` VALUES (806, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/998309413', '最近想入手一款笔记本电脑（本人大数据专业）各路大神有什么好的推荐吗？', NULL, '屏幕尺寸 ：14 裸机重量：1.6kg <em>神舟</em> 1.精盾 U45S1 屏幕尺寸 ：14 裸机重量：1.16kg 2.<em>战神k670C</em>-G6A1 屏幕尺寸 ：15 裸机重量');
INSERT INTO `commodity_review_info` VALUES (807, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/1102923551', '<em>神舟</em>k670T-G6A1，左风口出风贼大，D面贼热，D面右风扇不转。有没有方法控制风扇，非要拆机吗？', NULL, '这里建议先拆后盖，起个烤鸡程序看看风扇是不是右边不转，或者转不动，不排除右风扇硬件损坏');
INSERT INTO `commodity_review_info` VALUES (808, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/47544250', '大家觉得<em>神舟</em>电脑怎样啊！本人小白准备入手<em>神舟</em>k670，有谁告诉我怎样。还有k670是不是可以自己加msata啊。?', NULL, '是, 不过建议别加msata, 要加就加sata3 ssd, 然后hdd放2号仓');
INSERT INTO `commodity_review_info` VALUES (809, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/1093805543', '4000-4500,有什么电脑推荐，主要用于办公，不打游戏？', NULL, '机械革命S1 Pro和Acer宏碁宏碁蜂鸟Fun和<em>神舟战神K670C-G6A1</em>');
INSERT INTO `commodity_review_info` VALUES (810, '笔记本电脑', '神舟', '神舟战神K670C-G6A1', 'https://www.zhihu.com/answer/967582681', '春节想换台笔记本，大学生，玩cf,cs，其它游戏基本不会玩，平常会用电脑看视频网课，对续航要求6?', NULL, '续航我看官方说的是看视频不联网的条件下最长能看10小时（条件有点极端） 二、<em>神舟</em>(HASEE)<em>战神K670C</em>-G6A1 英特尔酷睿i5-9400 MX250 2G 15.6英寸游戏笔记本电脑(8G');
INSERT INTO `commodity_review_info` VALUES (811, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/683126366', '神州<em>战神</em>TX<em>9</em>-<em>CT5DK</em>怎么样？', NULL, '我来我来，刚买TX<em>9</em>不久的我。 玩游戏测试过几次 以下游戏无脑全拉最高。 逆水寒100-150那个分辨清晰度 APEX GTA5 守望先锋 剑网三 GPU温度稳定71-75');
INSERT INTO `commodity_review_info` VALUES (812, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/953323214', '<em>神舟战神GX9-CT5DK</em>与G9-CT7PK与什么不同，或者性能上有什么差异，还望解答？', NULL, '没有本质区别，<em>GX9</em>-<em>CT5DK</em>与G9-CT7PK都是17.3寸，屏幕144Hz IPS。显卡都是2070，而不是楼上说的其中一个为2060。而cpu无非是GX9用的台式机i5');
INSERT INTO `commodity_review_info` VALUES (813, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/950663777', '<em>神舟</em>可以上船吗？', NULL, '我5年<em>战神</em>用户，你这款处理器性能太强过剩了，推荐一款<em>gx9</em>-<em>ct5dk</em>，8300左右，2070显卡，你可以看看');
INSERT INTO `commodity_review_info` VALUES (814, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/713698357', '<em>神舟战神</em>到底稳不稳？', NULL, '为什么我身边的朋友都是神舟不好？ 为什么电脑城的人说神舟很垃圾用不了两天就坏了？ 为什么我姐或哥说别买神舟？ 为什么我姐的朋友(卖电脑的)说神舟很垃圾？ 我买的这款机子会不会很容易坏');
INSERT INTO `commodity_review_info` VALUES (815, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/839435296', '<em>神舟GX9</em>-CT7DK怎么样？', NULL, '笔记本很好，但是电源键是真的不方便，建议做成一个凸起的按钮,和键盘平行的设计总是让人难以按下。搞错了,是<em>CT5DK</em>，没关系，反正模具一样的。');
INSERT INTO `commodity_review_info` VALUES (816, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/954984368', '电脑小白求推荐1w左右的，能流畅运行pr，ps和剑三的笔记本电脑，重量无所谓，希望是不需要怎么打理的?', NULL, '评测】-京东 什么？你说价格太便宜？<em>9</em>k的也有，I79700+RTX2070【<em>神舟战神GX</em>】<em>神舟</em>(HASEE)<em>战神GX9</em>-<em>CT5DK</em> 英特尔酷睿i5-9400 RTX2070');
INSERT INTO `commodity_review_info` VALUES (817, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/795662606', '<em>神舟战神gx9</em>-ct7dk和暗影精灵5 2070max-q版本哪个更好？', NULL, '暗影精灵5plus！一定是plus！实在不行air也行。这两个散热改过的。 plus就是目前没货，也绝对值得等待。');
INSERT INTO `commodity_review_info` VALUES (818, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/758354751', '神州<em>GX9</em> <em>CT5DK</em>怎么样?', NULL, '如果要追求高配置的话，这是最便宜的rtx2070笔记本，散热也还不错，值得推荐');
INSERT INTO `commodity_review_info` VALUES (819, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/714188546', '<em>神舟</em>笔记本电脑<em>GX9</em>-CT7DK和TX<em>9</em>-CT7DK有什么区别吗？', NULL, '这两款电脑的配置基本一样，<em>gx9</em>重量5.85公斤，tx<em>9</em>重量4.78公斤，<em>gx9</em>的屏幕是17.3英寸的，tx<em>9</em>的屏幕是16.1英寸的窄边框。两个屏幕的特点也是一样的');
INSERT INTO `commodity_review_info` VALUES (820, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/817267302', '如何评价<em>神舟战神</em>的笔记本？', NULL, '如果我只有5000预算，需要买个笔记本打游戏，我会上船，因为这是最大程度利用这5000块钱，获得最高性能的唯一途径。 如果我有50000预算，需要买个笔记本打游');
INSERT INTO `commodity_review_info` VALUES (821, '笔记本电脑', '神舟', '神舟战神GX9-CT5DK', 'https://www.zhihu.com/answer/991371553', '2020年了10000元以下游戏本推荐?', NULL, '1、<em>神舟</em> <em>战神</em> TX<em>9</em>-<em>CT5DK</em> 价格：8799 i5-9400+RTX2070+16G内存+256G SSD+1T机械 【优势：搭载高素质16.1寸电竞屏，性价比极高】');
