INSERT INTO `commodity_review_info` VALUES (1088, '主板', '华硕', '华硕TUF B360M-PLUS GAMING S', 'https://www.zhihu.com/answer/976896211', '各位大佬，酷睿i3 8100 + <em>华硕</em> <em>TUF</em> <em>B360M</em>-<em>PLUS</em> <em>GAMING</em> <em>S</em>可以吗？', NULL, '不考虑多个ssd和未来升级高端u主板可以省下来200，9400f+主板套装也是你这个价钱，当然9400F没有核显');
