INSERT INTO `commodity_review_info` VALUES (1090, '主板', '华硕', '华硕TUF GAMING X570-PLUS ', 'https://www.zhihu.com/answer/1074206725', '<em>华硕TUF</em> <em>GAMING</em> <em>X570</em>主板有哪些优点与不足？', NULL, '是 <em>ASUS</em> <em>TUF</em> <em>GAMING</em> <em>X570</em>-<em>PLUS</em> 这款主板吧。 其实还可以的，不用对ASUS和前两年的TUF抱有太过分的成见，至少在我看来这款板子挺均衡。');
INSERT INTO `commodity_review_info` VALUES (1091, '主板', '华硕', '华硕TUF GAMING X570-PLUS ', 'https://www.zhihu.com/answer/760860191', '<em>X570</em>主板中<em>TUF</em> <em>X570</em> <em>PLUS</em> <em>GAMING</em> WIFI和 <em>X570</em>刀锋板相比怎么选择？', NULL, '<em>华硕</em>供电更好些，用的是Vishay SiC639，M11H/C/F同款。相比起EDGE的4C029/4C024高到不知道哪里去了');
